import * as OrderActions from './orders.action';
import {IProduct} from "../../Modules/products/Models/IProduct";
import {ADD_TO_CART_FAILURE, ADD_TO_CART_REQUEST, INCREMENT_PRODUCT_QTY} from "./orders.action";
import product from "../../../../server/Models/product";

export interface orderState {
    loading : boolean,
    cartItems : IProduct[],
    orders : IProduct[],
    errorMessage : string
}
let initialState : orderState = {
    loading : false,
    cartItems : [] as IProduct[],
    orders : [] as IProduct[],
    errorMessage : ''
}

export  const reducer = (state = initialState , action : any) : orderState => {
    switch (action.type) {
        case ADD_TO_CART_REQUEST :
            return {
                ...state,
                cartItems : [...state.cartItems , action.payload.product]
            }
        case ADD_TO_CART_FAILURE :
            return {
                ...state,
                errorMessage : action.payload
            }
        case INCREMENT_PRODUCT_QTY :
            let incrementItems = state.cartItems.map((product) => {
                if(action.payload.productId === product._id){
                    return{
                        ...product,
                        qty : product.qty + 1
                    }
                }
                return 
            })
        default : return state
    }
}
