import React, {useEffect} from "react";
import * as userReducer from '../../../../redux/users/user.reducer';
import {useDispatch, useSelector} from "react-redux";
import * as userActions from "../../../../redux/users/user.action";

interface IProps{

}

interface IState  {
    users : userReducer.UserState
}

let UserProfile : React.FC<IProps> = () => {
    let userState : userReducer.UserState = useSelector((state : IState) => {
        return state.users
    })
    let dispatch =useDispatch();
    useEffect(() => {
        dispatch(userActions.getUserInfo())
    },[])
    return(
        <React.Fragment>
            {/*<pre>{JSON.stringify(userState)}</pre>*/}
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-3">
                            <img src={userState.user.avatar} className="img-responsive img-thumbnail rounded-circle d-block m-auto"  alt=""/>
                        </div>
                        <div className="col-md-6">
                            <div className="card">
                                <div className="card-header bg-dark text-center text-white">
                                    Basic Details
                                </div>
                                <div className="card-body">
                                    <div className="list-group">
                                        <div className="list-group-item">Name : {userState.user.name}</div>
                                        <div className="list-group-item">Email : {userState.user.email}</div>
                                    </div>
                                </div>
                            </div>
                            <div className="card mt-5">
                                <div className="card-header bg-dark text-white">
                                    Address Details
                                    <button className='btn btn-danger btn-sm float-left'>Edit Address</button>
                                </div>
                                <div className="card-body">
                                    <div className="list-group">
                                        <div className="list-group-item">Flat : {userState.user.address.flat}</div>
                                        <div className="list-group-item">Street : {userState.user.address.street}</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
}

export default UserProfile;
